import React, {useEffect, useState} from 'react';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
  } from 'chart.js';
import { Bar } from 'react-chartjs-2';

ChartJS.register(
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend
  );

function Tweet() {
    useEffect( () => {
        fetchItems();
    }, []);

    const [items, setItems] = useState([]);

    const fetchItems = async () => {
        const data = await fetch('http://localhost:4000/data');
        console.log("harsha", data);
        const items = await data.json();
        setItems(items);
    };

    const options = {
        responsive: true,
        plugins: {
            legend: {
            position: 'top',
            },
            title: {
            display: true,
            text: 'Chart.js Bar Chart',
            },
        },
    };

    const labels = ['January', 'February', 'March', 'April', 'May', 'June', 'July'];
    const data = {
        labels,
        datasets: [
            {
            label: 'Dataset 1',
            data: items.map(row => row.voting_choice),
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
            }
        ],
    };


    return(
        <section>   
            <div class="container-fluid">
                <h1 class="mt-5">Votes Table</h1>
                {
                items.map(item => (
                    <div class="row padding">
                        <div class="alert alert-info rounded-pill" role="alert">
                            <i class="fa fa-user mr-2"></i> <i>{item.name} ({item.voting_choice}): {item.casted_at}</i>
                        </div>
                    </div>       
                ))
                }
            </div>
            <Bar options={options} data={data} />
        </section>
    );
}

export default Tweet;
