import React, {useEffect, useState} from 'react';
// import {Link} from 'react-router-dom';

function Tweet() {
    useEffect( () => {
        fetchItems();
    }, []);

    const [items, setItems] = useState([]);

    const [name, setName] = useState("");
    const [vote, setVote] = useState(false);
    const fetchItems = async () => {
        const data = await fetch('http://localhost:4000/votes');
        console.log("harsha", data);
        const items = await data.json();
        setItems(items);
    };
    const handleNameDetailsChange = (event) => {
        event.stopPropagation();
        event.target.value = event.detail.value
        let newValue = event.target.value;

        setName(newValue);
    }
    const handleVoteDetailsChange = (event) => {
        event.stopPropagation();
        event.target.value = event.detail.value
        let newValue = event.target.value;

        setVote(newValue);
    }
    function sendData(){
        // POST request using fetch()
        fetch("http://localhost:4000/addVote", {

        // Adding method type
        method: "POST",

        // Adding body or contents to send
        body: JSON.stringify({
            tweetInput: name,
            tweetVote: vote
        }),

        // Adding headers to the request
        headers: {
            "Content-type": "application/json; charset=UTF-8"
        }
        })

        // Converting to JSON
        .then(response => response.json())

        // Displaying results to console
        .then(json => console.log(json));
    }

    return(
        <section>

            <div class="container-fluid">
                <h1 class="mt-5">Votes</h1>
                <form>
                    <div class="input-group justify-content-center">
                        <div class="input-group-prepend">
                            <input type="text" name="tweetInput" class="form-control" value={name} onChange={(event) => handleNameDetailsChange(event)}/>
                            <input type="checkbox" name="tweetVote" class="form-control" value={vote} onChange={(event) => handleVoteDetailsChange(event)}/>
                            <input type="submit" value="Send" class="btn btn-primary mb-2" onClick={sendData()}/>
                        </div>
                    </div>
                </form>

                {
                items.map(item => (
                    <div class="row padding">
                        <div class="alert alert-info rounded-pill" role="alert">
                            <i class="fa fa-user mr-2"></i> <i>{item.name} ({item.voting_choice}): {item.casted_at}</i>
                        </div>
                    </div>       
                ))
                }
            </div>
        </section>
    );
}

export default Tweet;
