function Tweet() {
    const [items, setItems] = useState([]);
    const [barChartData, setBarChartData] = useState({});
    useEffect( () => {
        fetchItems();

        // Fetch bar chart data for overall results
        axios.get('http://localhost:4000/results').then((response) => {
        console.log('hey hi ',response.data.data);
        const counts = response.data.data.map((item) => item.count);
        console.log('hey hi ',counts);
        const choices = response.data.data.map((item) => item.voting_choice ? 'True' : 'False');
        console.log('hey hi ',choices);
        setBarChartData({
            labels: choices,
            datasets: [{ data: counts, backgroundColor: ['blue', 'red'] }],
        });
        });
        console.log('hey hi ',barChartData);
    }, []);

    const fetchItems = async () => {
        const data = await fetch('http://localhost:4000/data');
        console.log("harsha", data);
        const items = await data.json();
        setItems(items);
    };

    return(
        <section>   
            <div class="container-fluid">
                <h1 class="mt-5">Votes Table</h1>
                <Typography variant="h4">Polling System</Typography>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Name</TableCell>
                            <TableCell>Vote Choice</TableCell>
                            <TableCell>Date of Submission</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {data.map((item) => (
                        <TableRow key={item.id}>
                            <TableCell>{item.name}</TableCell>
                            <TableCell>{item.voting_choice ? 'True' : 'False'}</TableCell>
                            <TableCell>{item.casted_at}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </div>
            {/* <Bar options={options} data={data} /> */}
            <BarChart data={barChartData} />
        </section>
    );
}

export default Tweet;
